package com.karthik.core.datatypes;


public class maxValuePrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int maxa =  (int) Math.pow(2, 31);
	
	System.out.println(maxa);
	
	int mina= (int) Math.pow(2, 31)+1;
	
	System.out.println(mina);
	
	short maxs= (short) Math.pow(2, 15);
	System.out.println(maxs);
	short mins= (short) Math.pow(2, 15);
	System.out.println(mins);
	
	
//similarly for long max value is  2^63 -1 and min value is - 2^63
	
	
	}

}
